package DataStructureAndAlgorithms.src.gogogo;

import gogogo.ListNode;

import java.util.Arrays;

public class Day006 {
    public ListNode reverseList1(ListNode head) {
        //迭代法
        if (head == null || head.next == null) return head;
        ListNode pre = null, cur = head, temp = null;
        while (cur != null) {
            temp = cur.next;
            cur.next = pre;
            pre = cur;
            cur = temp;
        }
        return pre;
    }

    public ListNode reverseList(ListNode head) {
        //递归法 --
        if (head == null || head.next == null) return head;//递归的终止条件
        ListNode cur = reverseList(head.next); //递归
        head.next.next = head; //当前单元处理
        head.next = null;
        return cur;
    }

    public char findTheDifference(String s, String t) {
        //双指针
        char[] chars = s.toCharArray();
        char[] chart = t.toCharArray();
        Arrays.sort(chars);
        Arrays.sort(chart);
        int j = 0;
        for (int i = 0; i < s.length(); i++) {
            if (chars[i] == chart[j]) {
                j++;
            } else {
                break;
            }
        }
        return chart[j];
    }

    public static void main(String[] args) {
        Day006 day006 = new Day006();
        char theDifference = day006.findTheDifference("",
                "");
        System.out.println("" + theDifference);
    }


}
